(2, 'Phong thủy', 'Tranh phong thủy, loại tranh phong thủy được làm từ gạo', 'phong-thuy', '', 'logo.png', 'Tranh phong thủy được làm từ gạo chất liệu đơn giản nhưng mang hồn tranh độc đáo, một trào lưu tranh mới.', 'Tranh phong thủy', 'Tranh phong thủy được làm từ gạo chất liệu đơn giản nhưng mang hồn tranh độc đáo, một trào lưu tranh mới.', 1, 4),
(1, 'Nghệ thuật', 'Tranh đá quý nghệ thuật và các loại tranh nghệ thuật độc đáo', 'nghe-thuat', '', 'nghe-thuat.png', 'Nghệ thuật tranh gạo<br />', 'Nghệ thuật tranh', 'Nghệ thuật tranh gạo và các loại tranh nghệ thuật độc đáo mới lạ ', 0, 2),
(3, 'Thư pháp', 'Tranh thư pháp - tranh thư pháp đẹp', 'thu-phap', '', 'thu-phap.png', 'Tranh thư pháp - tranh thư pháp đẹp', 'Tranh Thư Pháp, Tranh chữ', 'Tranh thư pháp - tranh thư pháp đẹp', 1, 5),
(4, 'Động vật', 'Tranh động vật - các loại tranh về động vật', 'dong-vat', '', 'dong-vat.png', '<strong>Tranh động vật</strong>, tổng hợp các bức tranh&#160;về động vật<br />', 'Tranh động vật, Những mẫu tranh động vật', 'Tranh động vật, tổng hợp các bức tranh về động vật độc đáo.', 1, 6),
(5, 'Chân dung', 'Tranh chân dung do khách đặt - tranh chân dung đẹp', 'chan-dung', '', 'chan-dung.png', 'Bạn có thể đặt làm <strong>tranh gạo chân dung</strong> theo yêu cầu của mình, qua bàn tay khéo léo của các nghệ nhân chắc chắc bạn sẽ có một bức <strong>tranh gạo chân dung</strong> ưng ý.<br />', 'Tranh Chân Dung', 'Bạn có thể đặt làm tranh gạo chân dung theo yêu cầu của mình, qua bàn tay khéo léo của các nghệ nhân chắc chắc bạn sẽ có một bức tranh gạo chân dung ưng ý.', 1, 1),
(6, 'Phong cảnh', 'Tranh phong cảnh - tranh phong cảnh đẹp', 'phong-canh', '', 'phong-canh.png', '<strong>Tranh gạo phong cảnh</strong> được tổng hợp tất cả các bức tranh về phong cảnh các địa danh nổi tiếng trên cả nước hay đơn giản là những vùng quê việt xưa với dòng sông và con đò. Tất cả được tạo mô tả lại bằng bức <strong>tranh gạo</strong> bởi các nghệ nhân', 'Tranh phong canh, Tranh phong cảnh đẹp, Phong cảnh phòng khách', 'Tranh gạo phong cảnh được tổng hợp tất cả các bức tranh về phong cảnh các địa danh nổi tiếng trên cả nước hay đơn giản là những vùng quê việt xưa với dòng sông và con đò. Tất cả được tạo mô tả lại bằng bức tranh gạo bởi các nghệ nhân', 1, 0);

