(2, 'LG - Đầu bếp tương lai', 'Ông NGUYỄN KHẮC CHIẾN - Trợ lý TGĐ', 'Hà Nội ', 'www.binhminhwindow.com.vn', '<span font-family:="" font-size:="" font-style:="" line-height:="" text-align:="" background-color:="">Chúng tôi là doanh nghiệp có mạng lưới rộng trên cả nước. Khách hàng biết đến sản phẩm của chúng tôi chủ yếu qua website. Cám ơn&#160;</span><span font-family:="" font-style:="" line-height:="" text-align:="" background-color:="" font-size:="" font-weight:="">North Star&#160;</span><span font-family:="" font-size:="" font-style:="" line-height:="" text-align:="" background-color:="">đã cho chúng tôi sự yên tâm khi hợp tác cùng các bạn.</span>', 'lg-logo.jpg', 'binhminh-window.jpg', 'intro-duan-afc.jpg', 8, 1, 1, 52),
(3, 'Công ty Sơn Tison', 'Ông. TRƯƠNG LÂM - Tiến Sỹ và Chủ tịch HĐQT.', 'Hà Nội - Sài Gòn', ' www.txdthanhhoa.vn', '<span background-color:="" text-align:="" line-height:="" font-style:="" font-size:="" font-family:="">Tôi thấy các dịch vụ của </span><span font-size:="" background-color:="" text-align:="" line-height:="" font-style:="" font-family:=""><b>North Star</b></span><span background-color:="" text-align:="" line-height:="" font-style:="" font-size:="" font-family:="">&#160;đã và đang mang lại cho chúng tôi sự tin tưởng. Mong quý công ty cố gắng phát huy tốt hơn nữa để tạo được uy tín cho công ty và sụ tin tưởng từ khách hàng</span>', 'tison-paint-logo.jpg', 'thanhhoa.jpg', 'intro-duan-thammysao.jpg', 2, 1, 1, 124),
(4, 'Công ty triển lãm Allworld, Việt Nam', 'Ông. ĐINH NHỰT THUẬN - Giám Đốc', 'Tam Trinh - Hà Nội', 'www.universallogistics.vn', '<span font-weight:="" font-size:="" background-color:="" text-align:="" line-height:="" font-style:="" font-family:="">North Star&#160;</span><span background-color:="" text-align:="" line-height:="" font-style:="" font-size:="" font-family:="">đã thực hiện yêu cầu của công ty chúng tôi trong thời gian sớm nhất, đội ngũ nhân viên nhiệt tình</span>', 'mining-vietnam-logo.jpg', 'universal-ykien.jpg', 'intro-duan-tuongphat.jpg', 3, 1, 1, 52),
(5, 'Vietlandscape Travel', 'Chị HỒ THỊ KIM LOAN - General Director.', 'Hà Nội ', 'www.vietlandscape.com.vn', '<span background-color:="" text-align:="" line-height:="" font-style:="" font-size:="" font-family:="">Lúc đầu tôi đã quyết định hợp tác với&#160;</span><span font-weight:="" background-color:="" text-align:="" line-height:="" font-style:="" font-size:="" font-family:="">North Star&#160;</span><span background-color:="" text-align:="" line-height:="" font-style:="" font-size:="" font-family:="">vì tôi tin tưởng vào uy tín cũng như phong cách làm việc rất sáng tạo, cùng với một đội ngũ nhân viên nhiệt tình đã làm cho tôi rất hài lòng với quyết định của mình.&#160;</span><span font-weight:="" background-color:="" text-align:="" line-height:="" font-style:="" font-size:="" font-family:="">North Star</span><span background-color:="" text-align:="" line-height:="" font-style:="" font-size:="" font-family:="">&#160;chính là một lựa chọn đúng đắn của tôi.</span>', 'hanna-instruments-logo.jpg', 'vietlandscape.jpg', 'intro-duan-andat.jpg', 4, 1, 1, 52),
(6, 'Du lịch Hoàn Mỹ', 'Ông NGUYỄN THẾ KHẢI - Giám Đốc', 'Hà Nội ', ' www.dulichhoanmy.com', '&#160;<span>Đội ngũ của&#160;</span><span>North Star&#160;</span><span>tuy còn rất trẻ nhưng làm việc rất chuyên nghiệp và tận tâm, nhất là khâu bảo trì website sau khi nghiệm thu. Tôi hoàn toàn hài lòng với trang web hiện tại của công ty</span><span>.</span>', 'samhoa-logo.jpg', 'hoanmy.jpg', 'intro-duan-giaidieuxanh.jpg', 0, 1, 1, 52),
(7, '9Wall - Free Wallpapers', 'Chị NGUYỄN MINH THẢO - Director.', 'Hà Nội ', ' www.9wall.com', '&#160;<span>Tôi biết đến&#160;</span><span>North Star</span><span>&#160;qua sự giới thiệu của một người bạn đã tin tưởng và sử dụng dịch vụ của&#160;</span><span>North Star</span><span>. Tôi rất thích cách làm việc của các bạn ở&#160;</span><span>North Star</span><span>, họ trẻ và năng động chính vì vậy họ có những ý tưởng rất sáng tạo. Tôi rất hài lòng với website hiện nay của mình</span>', 'nhanhoanghia-logo.jpg', '9wall.jpg', '', 7, 1, 0, 88),
(8, 'Công ty Bảo Thiên An', 'Ông HOÀNG LÊ NHU - Giám Đóc', 'Hà Nội ', 'www.baothienan.com', '&#160;<span>Đối với nhều khách hàng, hiện nay có rất nhiều chọn lựa để có thể có thể thiết kế một website đẹp nhưng điều mà tôi quan tâm chính là chất lượng dịch vụ và chăm sóc khách hàng - một yếu tố rất quan trọng tạo cho bạn sự an tâm nhất định cho việc phát triển lâu dài - tôi đã chọn và ưng ý với&#160;</span><span>North Star</span><span>&#160;về điều này</span>', 'ruou-tan-binh-minh-logo.jpg', 'baothienan.jpg', 'intro-duan-thienthannho.jpg', 5, 1, 1, 88),
(9, 'Công ty Đan Long', 'Ông. LÊ THANH BÌNH - Manager', 'Hà Nội ', 'www.lotuslogistics.com.vn', '<div><span background-color:="">North Star</span><span font-weight:="" font-size:="" background-color:="" text-align:="" line-height:="" font-style:="" font-family:="">&#160;</span><span background-color:="" text-align:="" line-height:="" font-style:="" font-size:="" font-family:="">is one of best providers on website designation. Its after-sales service is good&#160;</span></div>', 'aup-logo.jpg', 'lotus-logistic.jpg', 'anhkhoi-intro-duan.jpg', 6, 1, 1, 52),
(10, 'Shop hoa andy ', 'Nguyễn Hoàng', '317 Trường Chinh', 'http://andy.vn/', 'Tôi rất hài lòng về dịch vụ của North Star<br />', 'logo.png', 'tieu-su-nguyen-bao-hoang-120312-21.jpg', 'tee-tick-intro-duan.jpg', 9, 1, 1, 52);

