INSERT INTO `tbl_education` (`edu_id`, `edu_info`, `edu_full`, `edu_status`) VALUES
(1, '<em><strong>Được thành lập từ năm 2012, Tranh Gạo Việt chuyên kinh doanh tranh gạo mang đậm nét văn hóa Việt Nam với các loại tranh phong cảnh, tranh phong thủy và tranh nghệ thuật, tranh treo tường...<br />\r\n<br />\r\n<br type="_moz" />\r\n</strong></em>', '<span>Ở thể loại</span><a target="_blank" href="http://www.tranhgaoquynhvy.com/vi/tranh-cat/tranh-chan-dung.html">&#160;t</a>ranh gạo<span>, bằng bàn tay khéo léo và sự lao động sáng tạo, những nghệ nhân tại Tranh Gạo Việt đã cho ra đời hàng trăm bức tranh phong cảnh, thư pháp, tĩnh vật… và công phu nhất là những tác phẩm&#160;</span>tranh chân dung<span>&#160;từ những hạt gạo từ thiên nhiên.<br />\r\n<br />\r\n</span>\r\n<p><img alt="CD51" src="http://tranhgaoviet.com/uploads/products/cha-me.jpg" height="361" width="500" /><br />\r\n&#160;</p>\r\n<p>Với mục đích quảng bá rộng rãi hơn nữa những giá trị của tranh cát và mô phỏng lại quá trình tạo nên tác phẩm độc đáo này, tranh gạo việt cần tìm thêm cộng tác viên bán hàng và tìm các đại lý phân phối sản phẩm tranh gạo.&#160;<br />\r\n<br />\r\nNếu đối tác nào quan tâm xin vui lòng liên hệ với chúng tôi theo địa chỉ<br />\r\n<br />\r\nSố 22, Ngõ 49/165 đường Dương Quảng Hàm, quận Cầu Giấy, Hà Nội<br />\r\nSố hottline : 0974136509<br />\r\nEmail : haanhdon@gmail.com</p>', 0);

